const updateClassName=(a,e,s=null,...c)=>{switch(a){case"add":c.forEach((a=>{e.classList.add(a)}));break;case"remove":c.forEach((a=>{e.classList.remove(a)}));break;case"replace":1===c.length&&s&&e&&e.classList.replace(c[0],s);break;case"toggle":c.forEach((a=>{e.classList.toggle(a)}))}};export default updateClassName;